css:
@-moz-document url-prefix("https://avenoel.org/")
{

.message-username a
{
display:none;
}
.message-username:after
{
content:'NPC';
color:black;
}
.message-avatar img
{
visibility:hidden;
}
.message-avatar
{
background:url(http://image.noelshack.com/fichiers/2018/43/7/1540730370-npc.jpeg) left top no-repeat;
}
.message-content-quote-author
{
display:none
}
.message-content-quote-caption:after
{
content:'NPC';
}
.topics-author a
{
display:none
}
.topics-author:after
{
content:' NPC';
}
.message-infos
{
display:none
}
.message-avatar {
height: 100%;
}

}
