css:
@-moz-document domain("tseirptranslations.com") {
body {
    background-color: #000 !important;
    color: #CCC;
}
.header .footer {
    background-color: #000;
}
.navigation {
    background-color: #000;
}
.bg-white {

    background: #000;
}
.main-menu > .menu-item-has-children > a {

    color: #ccc;
}
.main-menu a {

    color: #ccc;
}

.textwidget {

    color: #fff;
}
.blog-title a {

    color: #FFF;
}
.blog-tagline {
    color: #FFF;
}
.post {

    background: #2d2d2d;
    border: 1px solid #2f2f2f;
    color: #979797;
}

.post-title a {
    color: #F1f1f1;
}
.lovecraft-widget-list .title {
    color: #CCC;
}

.widget_archive li a,
.widget_categories li a,
.widget_meta li a,
.widget_nav_menu li a,
.widget_pages li a {

    color: #ddd;
}

.comment-inner {
    background: #1e1e1e;
}

.comment-header h4 a {
    color: #979797;
}

.comment-respond input[type="text"],
.comment-respond textarea {
    background: #181818;
    color: #FFF;
}
.comment-respond form label {
    color: #ccc;
}
}
