css:
@-moz-document domain("fishki.net") {
.header.position-fixed {
    display: none;
}
.sidebar.sidebar--right {
    display: none;
}
.content {
    width: 100% !important;
}
.other-post-list {
    display: none;
}
.see_other_div {
    display: none;
}
.footer {
    display: none;
}
.padding_footer {
    display: none;
}
.header-spacer {
    display: none;
}
.content__icons {
    display: none;
}
.content__tags {
    margin-bottom: 0px;
}
.content__figure__share {
    display: none;
}
.not-logged {
    display: none;
}
.content__sub-heading {
    display: none;
}
.social-line {
    display: none;
}
.the-face {
    display: none;
}
.button_add_community {
    display: none;
}
form[class^="idform_add_comment"] {
    display: none;
}
.comment__avatar .avatar-a img {
    filter: none;
}
p.source {
    display: none;
}
.slider--wide .slide__item img {
    filter: blur(25px);
    width: 50%;
    display: initial !important;
}
.slider--wide .slide__item img:hover {
    filter: none;
}
.fishki-post-list .slider--wide .slide__item img {
    filter: blur(25px);
    width: 50%;
    display: initial !important;
}
.fishki-post-list .slider--wide .slide__item img:hover {
    filter: none;
}
.fishki-post-list .slider--wide .slide__item .picture-holder {
    text-align: center;
}
.comments img {
    filter: blur(25px);
    width: 50%;
    display: initial !important;
}
.comments img:hover {
    filter: none;
}
img.small {
    display: none;
    visibility: hidden;
    position: absolute;
}
.content__row.content__row--primary.block_fit_primary {
    display: none;
    visibility: hidden;
}

.slide__info {
    display: none;
}
}
