css:
@-moz-document url-prefix("http://orteil.dashnet.org/cookieclicker/"), url-prefix("https://orteil.dashnet.org/cookieclicker/") {
.product {
    height: 41px;
}

.product .icon {
    height: 41px;
}

.product .title {
    font-size: 16px;
}

.product .lockedTitle {
    font-size: 16px;
}

.product .content .owned {
    font-size: 32px;
    font-family: Inconsolta;
    font-weight: Bold;
    padding:0px;
    margin:0px;
    right: 5px;
    top: 3%;
}

.product .content .price {
    font-size: 11px;
    top: 0%;
}

.product .content .price::before {
    top: 0px;
}

#store::after {
    display: none;
}

#support {
    display: none;
}

#storeTitle {
    display: none;
}

#centerArea {
    overflow-y: auto;
}

#sectionRight {
    overflow-y: auto;
    padding-right:17px;
}

#commentsTextBelow {
    display:none;
}

#commentsText {
    display:none;
}

#ascendMeterContainer {
    display:none;
}

#ascendNumber {
    right:120px;
    top:6px;
    font-family:Inconsolta;
}

#grimoireBarText {
    font-family:Inconsolta;
    font-weight: bold;
    font-size:120%;
}

#upgrades{
    padding-top:0px;
}

#toggleUpgrades{
    padding-top:0px;
}
}
