css:
@-moz-document domain("atlasobscura.com") {
html,
body,
code,
pre,
.nav-verticals {
    background: none !important;
    background-color: #252525 !important;
    border: none !important;
    color: #999 !important;
    text-shadow: none !important;
}
a.logo-link .icon-atlas-logo-alt {
    color: #86887b;
}
#nav-profile-menu .user-icon,
.nav-search .icon-search {
    color: white;
}
.homepage-gastro-callout .food-card .gastro-item-text,
.content-card-place {
    background-color: #191929 !important;
}
.site-top-notification-link,
#btf-nav {
    display: none !important;
}
.nav-right-section,
.nav-left-section,
.nav-center-section,
.dropdown,
.user-cell,
header {
    border: none !important;
}
.secondary-nav-verticals {
    border-bottom: 1px solid gray;
}

h1,
h2,
h3,
h5,
h5,
.title-underline {
    color: #a28344 !important;
}

p,
.content-card-subtitle,
.nav-link,
a,
.subtitle-sm {
    color: #80816f !important;
}
}
