css:
@-moz-document domain("forums.xamarin.com") {
#Frame,
body {
    background-color: #232323;
}

.ChildCategories-Title,
.ChildCategoryList,
.DataCounts,
.DataList,
.DataList.Comments,
.DataTable,
.Discussion .CommentForm,
.MessageForm,
.MessageList.Discussion .ItemDiscussion,
.PanelColumn .BoxFilter,
.SearchForm,
body.Profile .PanelColumn > .Photo,
.MessageList.Comments > .Item {
    background: #191818;
}
.ItemDiscussion .Message,
.MessageList .Message {
    color: #2f2f2f;
}
pre,
code {
    background: #3a3a3a;
}
.DataTable .Item:nth-child(odd) td {
    background-color: #292929 !important;
}
.pln,
.pun,
.lit,
p,
b,
li {
    color: #fff;
}
.com {
    color: #1ea716
}
.typ {
    color: #1bf7da;
}
}
