css:
@-moz-document url-prefix("http://kissmanga.com/Manga/") {
#containerRoot > div:nth-child(4) > div:nth-child(1) > div:nth-child(3) {
    position: fixed;
    right: 350px;
    top: 2px
}
#headnav {
    position: fixed;
    top: -60px;
}
#containerRoot > div:nth-child(4) > div:nth-child(1) > div:nth-child(1) {
    float: left;
    position: fixed;
    top: 2px;
    right: 430px;
}

#containerRoot {
    padding-top: 25px;
}
#navsubbar {
    background: rgba(0, 0, 0, .68);
    height: 29px
}
#navsubbar > p {
    margin: 0px
}
}
