css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
    /* Normal post avatars */
    div.UIIntentionalStory a.UIIntentionalStory_Pic {
        display: none !important;
    }
    /* Own avatar near "What's on your mind box" */
    div.UIComposer a.UIComposer_Pic {
        display: none !important;
    }
    /* Resize status update composer to full width */
    .UIComposer_STATE_PIC_OUTSIDE .UIComposer_UIContentBox { 
        margin-left: 0px !important;
    }
    /* Avatars in wall posts. TODO: May be obsolete */
    div.wallpost div.wallimage {
        display: none !important;
    }
    /* Avatars in comments */
    div.comments_list_wrapper div.comment_profile_pic {
        display: none !important;
    }
    /* Own avatar beside the compose comment box */
    div.comments_add_box div.comments_add_box_image {
        display: none !important;
    }
    /* Wider comment text box */
    .comments_add_box textarea {
        width: 340px !important;
    }
    /* Use full width for the "What's on your mind box" */
    div.UIComposer_UIRoundedBox {
        padding-left: 0 !important;
    }
    /* Wider space for the posts */
    div.UIIntentionalStory_Body {
        padding: 0 0 0 20px !important; 
    }
    /* Remove padding in the feed stream */
    .UIIntentionalStory {
        padding-left: 0px !important;
    }
}
