css:
@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("http://web.roblox.com"), url-prefix("https://web.roblox.com"), url-prefix("http://de.roblox.com"), url-prefix("https://de.roblox.com")
{
  #header
  {
    background-color: #BDBDBD;
  }

  .navigation .notification-icon
  {
    background: #BDBDBD;
  }

  .forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th
  {
    background: #BDBDBD!important;
  }

  .rbx-upgrade-now #upgrade-now-button,
.rbx-btn-secondary-xs.btn-more,
#AjaxCommentsContainer > div > div.comment-form > form > button
  {
    background-color: #BDBDBD!important;
    border-color: #BDBDBD;
  }

  .rbx-upgrade-now #upgrade-now-button:hover,
.rbx-btn-secondary-xs.btn-more:hover,
#AjaxCommentsContainer > div > div.comment-form > form > button:hover
  {
    background: #FF17E9!important;
    border-color: #BDBDBD;
  }

  #MasterContainer,
.nav-content-inner,
.container-main,
.gutter-ads
  {
    background: url(https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTIJLqhehfvQF1NpACzTAg4dn437V4PTVA2ynuEopgxwev-8qAf) top center repeat-x #F3F4F9;
  }

  #navbar-search-input
  {
    border: 1px solid #BDBDBD;
  }

  #party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off
  {
    background: linear-gradient(tobottom, #BDBDBD, #BDBDBD);
  }

  body
  {
    font-family: Segoe UI;
  }

  #GamesPageLeftColumn
  {
    width: auto;
    margin-right: 10px!important;
    margin-left: 10px!important;
  }

  #navigation .rbx-lead
  {
    margin-top: 10px;
  }
    /* Credit */
  .rbx-left-col:before
  {
    position: absolute;
    color: #9F7B9D;
    font-size: 9pt;
    font-family: Arial, Helvetica, sans-serif;
    content: "Theme by NintendoCube.";
    top: 0px;
    left: 11px;
    z-index: 1050;
  }

  #GamesListsContainer > div
  {
    background-color: transparent!important;
    width: 100%!important;
  }

  img[src*="/images/Forums/thread-unread.png"]
  {
    content: url("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTIJLqhehfvQF1NpACzTAg4dn437V4PTVA2ynuEopgxwev-8qAf");
  }

  img[src*="/images/Forums/thread-read.png"]
  {
    content: url("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTIJLqhehfvQF1NpACzTAg4dn437V4PTVA2ynuEopgxwev-8qAf");
  }

  img[src*="/images/Forums/popular-read.png"],
img[src*="/images/Forums/popular-unread.png"]
  {
    content: url("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTIJLqhehfvQF1NpACzTAg4dn437V4PTVA2ynuEopgxwev-8qAf");
  }

  img[src*="/images/Forums/locked-unread.png"]
  {
    content: url("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTIJLqhehfvQF1NpACzTAg4dn437V4PTVA2ynuEopgxwev-8qAf");
  }

  img[src*="/images/Forums/locked-read.png"]
  {
    content: url("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTIJLqhehfvQF1NpACzTAg4dn437V4PTVA2ynuEopgxwev-8qAf");
  }

  #TopAbpContainer,
.ad-annotations,
#LeftGutterAdContainer,
#RightGutterAdContainer,
#Skyscraper-Adp-Right,
#Skyscraper-Adp-Left,
#ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(3),
#Body > table > tbody > tr > td:nth-child(4)
  {
    display: none;
  }
}
