css:
@-moz-document url-prefix("https://www.bing.com/search?") {
* {
	font-family: Google sans,"roboto", tahoma, arial, sans-serif !important;
}
#b_results > li a:visited {
	color: #9032BF;
}
#b_results > li a {
	color: #4A8AF4;
}
#b_context {
	display: none;
}
h2,
h2.b_topTitle,
.b_algo .b_deep h3 {
	font-size: 18px;
}
strong {
	color: #dd4b39;
	font-style: normal;
	font-weight: normal;
}
#b_content {
	padding: 36px 0 20px 146px;
}
body,
#b_results > li {
	background: rgb(249, 249, 249);
}
p {
	color: #8E8E8E;
}
.b_scopebar {
	margin: 25px 0 0 150px;
}
form {
	margin: 10px 0 0 45px;
}
.b_scopebar {
	margin: 20px 0 0 150px;
}
.b_active a {
	color: #de3700 !important;
}
.b_scopebar a {
	color: #777 !important;
}
.b_scopebar li {
	padding: 5px 8px 9px 8px;
	line-height: 22px;
}
.b_deep,
#b_results .b_caption,
#b_results .b_snippet,
#b_results .b_attribution,
#b_results .b_factrow,
#b_results p,
#b_context .b_caption,
#b_context .b_snippet,
#b_context .b_attribution,
#b_context .b_factrow,
#b_context p,
#snct {
	line-height: 1.5;
}
#b_header {
	background-color: #f1f1f1;
	padding: 37px 0 0 0;
}
.b_searchboxForm,.b_searchboxForm:hover {
	border-radius: 20px;
}
body .b_searchboxForm .b_searchboxSubmit {
    border: 0px !important;
}
}

@-moz-document url-prefix("https://www.bing.com/") {
body .b_searchbox {
	background-color: transparent;
}
body .b_searchboxForm {
	background-color: #ffffffb0;
}
body .b_searchboxForm .b_searchboxSubmit {
	border: 8px solid #fff0;
	background-color: #fff0;
}
#b_footerItems,
#b_footer {
	display: none;
}
#focus_ovr{
height:100% !important;	
}
}
