css:
@-moz-document domain("askubuntu.com") {
html,
body,
code,
pre {
    background: none !important;
    background-color: #000009 !important;
    border: none !important;
    color: #999 !important;
    text-shadow: none !important;
}

.topbar .topbar-links .search-container input[type="text"] {
    background: none !important;
    background-color: #000009 !important;
    color: #999 !important;
}

.question-hyperlink,
.answer-hyperlink,
div,
span,
p,
h1,
h2,
h3,
h4,
table,
tr,
td,
th,
ul,
li,
input,
select,
footer,
header,
nav {
    background: none !important;
    background-color: #081016 !important;
    border: none !important;
    color: #778 !important;
    box-shadow: none !important;
    text-shadow: none !important;
}

hr {
    background-color: #000009 !important;
}

.arrow-up {
    background: transparent !important;
}

a {
    border: none !important;
}

img {
    opacity: .7 !important;
    transition: opacity .2s;
    border-radius: 0.5em !important;
}
img:hover,
a:hover img {
    opacity: 1 !important;
    border-radius: 0.5em !important;
}
.post-tag {
    color: #772953;
    background-color: #9e908b;
}
h3 a.question-hyperlink,
h1 a.question-hyperlink {
    color: #e95420 !important;
}
}
