@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mixi.jp") {

#headerArea,
#bodyArea,
#footerArea
{
 width:2048px !important
}

ul.personalNaviHome {
 background:#ffebc6 !important;
}
ul.personalNaviHome li
{
 margin:0 50px !important;
}

#bodyMainArea {
 width:1816px !important;
}
#bodyContents {
 width:1540px !important;
}

div.heading15 {
 width:1520px !important;
 background:#f7f7f7 !important;
 letter-spacing:1em;
}
dl.contentsList01 dt {
 width:150px !important;
 letter-spacing:1em;
}
dl.contentsList01 dd {
 letter-spacing:1em;
 width:1000px !important;
}
div#mixiInfo div.contents {
 background:#fff !important;
}
div#mixiInfo div.contents ul {
 background:#fff !important;
 padding-left:1em !important;
 letter-spacing:1em;
 font-size:150%;
 width:1000px !important;
}
div#mixiInfo {
 border:4px solid #c6dff7;
}

}
