css:
@-moz-document url-prefix("https://skripthub.net/docs/") {
:root{
  --background-color:#111;
  --accent-color:#444;
  --text-color:#fff;
}

.ui.celled.grid, .ui.violet.message, .ui.celled.grid > .row{
  background-image:none!important;
  background-color:var(--background-color)!important;
}

.SyntaxItemCard{
  background-image:none!important;
  background-color:var(--accent-color)!important;
}

.ui.fluid.accordion, .ui.styled.accordion > .title:first-child, .SyntaxItemCard, .eventvaluesblock, .infoboxblock, .typeusageblock{
  background-image:none!important;
  background-color:var(--accent-color)!important;
}

.syntaxtitle, .syntaxdescription, .ui.styled.accordion > .title:first-child, .addonname, .voteCountText, .ui.icon.input > i.icon:not(.link), h1, h2, h3, h4, h5, .eventvaluestext, .infotext, .typeusagetext, .ui.action.input:not([class*="left action"]) > input{
  color:var(--text-color)!important;
}
}
