css:
@-moz-document domain("ionicframework.com") {
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    background-color: #F5F5F5;
}
::-webkit-scrollbar {
    width: 8px;
    background-color: #F5F5F5;
}
::-webkit-scrollbar-thumb {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .3);
    background-color: #555;
}
body {
    background-color: #333333;
}
docs-header {
    background-color: #3880ff;
}
docs-menu {
    background-color: #333333;
}
docs-header,
docs-header a.link-active {
    color: white;
}
.Dropdown-panel {
    background-color: #3880ff;
}
.Logo,
docs-menu header .Logo {
    color: white;
}
.SectionNav .Dropdown-button {
    color: white;
}
docs-nav li header {
    color: white;
    font-weight: bold;
}
docs-nav li ul li {
    color: #e3e3e3;
}
docs-nav a.link-active {
    color: #3880ff;
}
docs-document > :first-child {
    color: white;
}
docs-page p {
    color: #d3dae3;
}
docs-page h1 {
    color: white;
}
docs-page h2 {
    color: #eeeeee;
}
docs-page h3 {
    color: #dddddd;
}
docs-page h4 {
    color: #cccccc;
}
docs-page h5 {
    color: #bbbbbb;
}
docs-page strong {
    color: white !important;
}
docs-page .intro strong {
    color: white;
}
docs-page .intro {
    color: #8a99c5;
}
docs-page ul {
    color: #d3dae3;
}
docs-page ol {
    color: lightgray;
}
docs-page blockquote {
    background-color: #635d4c;
    color: white;
}
docs-page dd {
    color: #d3dae3;
}
docs-page dt strong {
    color: white;
}
docs-page tbody tr:nth-child(even) {
    background-color: lightgray;
}
docs-page thead,
docs-reference thead tr {
    background-color: #3880ff;
    color: white;
}
docs-reference tbody tr td div p {
    color: black;
}
.Card {
    background-color: #3f4244;
}
.others {
    background-color: #3c3f41;
}
.others span {
    color: white !important;
}
.Card-header {
    color: white;
}
card-link .card-link__body {
    color: white;
}
.menu__item {
    color: white;
}
.migration-pill {
    color: #6974ff;
}
.api-version-selector {
    color: white;
}
}

@-moz-document domain("capacitor.ionicframework.com"), domain("forum.ionicframework.com"), domain("blog.ionicframework.com") {
body {
    background-color: transparent;
}
}
