css:
@-moz-document domain(hao123.com) {
            #skinroot .footerCont,
            #skinroot #navrecommend-top,
            #skinroot #pageMenu,
            #skinroot #hao123-govsite,
            #skinroot #hotsearch-box,
            #skinroot #noticeLink,
            #skinroot #notice,
            #skinroot .hot-num,
            #skinroot #layout-side,
            #skinroot #skinbtn,
            #skinroot #shortcut-box,
            #skinroot #hao123-bottom,
            #skinroot #slidetoolbarContainer,
            #skinroot #mingzhanresource,
            #skinroot #layout-hlbar,
            #skinroot #guesslikesite-block,
            #skinroot #hao123-guesslike,
            #skinroot #append,
            #skinroot #navrecommend-hjw {
                display: none!important;
            }
            #skinroot .sam-A #header {
                display: none!important;
            }
            #skinroot .hao123-search-panel-box {
                display: none!important;
            }
            #skinroot #layout-content {
                margin: 15px auto!important;
                float: inherit!important;
            }
            #skinroot .js_item-add,
            #skinroot .box-famoussite,
            .new-coolsite-bar .bar-text{
                display: none!important;
            }
            #skinroot .layout-container-inner>div:first-child,#skinroot .layout-container-inner>div:nth-child(2){
                display:none;
            }
            #skinroot .layout-container,#skinroot,html,body{
                overflow:hidden;
                height:100vh;
            }
            #skinroot .layout-container-inner{
                height: 100vh;
                overflow: auto;
            }
            #skinroot .layout-famoussite{
                box-shadow: 5px 5px 10px 1px rgba(0, 0, 0, 0.2);

                position: sticky;
                top:15px;
            }
            iframe{
                display:none;
            }
            .popup-site-w{
                left: 50%!important;
                transform: translate(-50%,0)!important;
            }
            .new-coolsite-bar{
                background:transparent;
            }
            #skinroot .layout-container{
                width:100%;
                background: no-repeat url(https://source.unsplash.com/random/1920x1080?nature,water);
                background-size:cover;
            }
            #skinroot #layout-content>*{
                background: rgba(255,255,255,.7);
            }
            #skinroot #layout-content>*:hover{
                background: #fff;
            }
            #skinroot .layout-container-inner,#skinroot .page-bg{
                background:transparent;
            }
            #skinroot a.sitemainlink[data-title="掘金"]{
                background-image:url(https://juejin.im/favicon.ico)!important;
                background-size:16px;
            }
        }
