css:
@-moz-document url-prefix("https://workflowy.com") {
body {
	background-color: #181818;
}

.bullet {
    filter: brightness(2);
}

.children {
    border-left:1px solid #444;
}

#controls > * {
    filter: brightness(2)
}

#documentView {
	color:#d8d8d8;
}

.noted > .notes > .content  {
    color: rgb(131, 141, 147);
}

.page {
	background-color: rgb(37, 50, 57);
}

.project.addedToSelection, body.windowBlurred .project.addedToSelection {
    background: rgb(49, 65, 77);
}

.project.highlighted::before {
    background: rgb(41, 55, 63);
}

.project > .name > .bullet {
    background-color: #333;
}

.project.open > .name > .bullet {
    background-color: transparent;
}

.project.shared > .name > .bullet {
    background-color: #304848;
}

}
