css:
@-moz-document domain("krebsonsecurity.com") {
body {
    background: #292929;
}
p, h2 a, .post h2, .post h
, .entry li, #sidebar h2 {
    color: #d5d5d5;
}
ol.commentlist li.even, comment.even.thread-even.depth-1, #content, .container_16 {
    background: #292929;
}
ol.commentlist li.odd {
    background: #2f2f2f;
}
a, body, ol.commentlist li div.comment-meta a:hover, ol.commentlist li div.vcard cite.fn a.url:hover, span.date {
    color: #4c7bf3;
}

ol.commentlist li div.vcard cite.fn a.url, ol.commentlist li div.vcard cite.fn, .grid_11 h3, #commentform small, .commentbody p {
    color: #f1f1f1;
}
#pgnav li a {
    background-color: #2f2f2f;
    color: #4c7bf3;
}
}
