css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.conquerclub.com") {
html, body {background-color:#000000 !important;}

/* Set basic background and border color */
#footer, #masthead, #rightColumn, #leftColumn {background-color:#333333 !important; border-color:#333333 !important;}

/* Set slightly lighter background color */
a {color:#000000 !important;}
#headerline, .hnav, .hnav ul li a:link, .hnav ul li a:visited, .vnav ul li a:link, .vnav ul li a:visited, .vnav ul li a:active {background-color:#666666 !important; border-color:#333333 !important; color:#000000 !important;}

/* Set lightest background color */
#middleColumn, #outerColumnContainer, .hnav ul li a:hover, .vnav ul li a:hover {background-color:#C0C0C0 !important;}
#pageWrapper, #masthead, #innerColumnContainer, #footer, .vnav ul, .vnav ul li, .hnav, .hnav ul li a {border-color:#C0C0C0 !important;}
#outerColumnContainer {border-left-color:#333333 !important;border-right-color:#333333 !important;}

/* no cutesyclub */
#masthead img {display:none;}
a.rank.r3p {visibility:visible !important;}
}
