css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("codeproject.com")
{
  .blank-background, .navmenu, .site-header, .rating-stars-medium {
    background: #202020 !important;
  }
  body {
    background: #202020 !important;
  }
  .container-content {
    background: #202020 !important;
  }
  .container-article {
    background: #202020 !important;
  }
  .container-article p, strong, em, .selected, h1, h2, li, .date span, .subject-line .author a, td, .description, .author-wrapper .container-member .author, .searchbar, .date {
    color: #d8d8d8 !important;
  }
  code, code strong, code em {
    color: #FF9900 !important;
  }
  .code-keyword {
    color: #0079ff;
  }
  .code-string {
    color: #ff9900;
  }
  .code-digit {
    color: #569cd6;
  }
  .desktop-only, .navmenu li, .navmenu ul, .navmenu a, .navmenu span, .tooltip-flyout, pre, .search, .search td, .hover-container, .search-advanced, #ForumTable table table, .indent {
    background: #282828 !important;
    color: #d8d8d8 !important;
  }
  .member-profile {
    background: #282828
  }
  .member-rep-box {
    background: white;
  }
  .navmenu li:hover, .navmenu li a:hover, .navmenu li a:hover span {
    background: #FF9900 !important;
    color: #fefefe !important;
  }
  .hover-row.hover, .hover-row.hover td, .hover-row:hover, .hover-row:hover td, tr.selected:hover .subject-line td {
    background-color: #FF9900 !important;
  }
  .ArticleTable td, .ArticleTableNoBorder td, table.article td, .ArticleTable th, .ArticleTableNoBorder th, table.article th {
    background-color: #282828 !important;
    color: #D8D8D8 !important;
  }
  .small-text {
    color: #fefefe;
  }
}
