css:
/*
*A dark mode for Hudson's FTM Guide
*/

@-moz-document domain("ftmguide.org")
{

body {
	background-color: #414141;
}

p {
	font-family: Verdana, Arial, Helvetica, sans-serif;
	font-style: normal;
	line-height: 15px;
	font-weight: normal;
	color: #FFFBD4;
	font-size: 12px;
}
h3 {
	font-family: Verdana, Arial, Helvetica, sans-serif;
	font-size: 12px;
	line-height: normal;
	font-weight: bolder;
	font-style: normal;
	color: #FFFBD4;
}
h4 {
	font-family: Verdana, Arial, Helvetica, sans-serif;
	font-size: 10px;
	font-weight: normal;
	line-height: normal;
	color: #FFFBD4;
}
h2 {
	font-family: Verdana, Arial, Helvetica, sans-serif;
	font-size: 12px;
	font-style: normal;
	line-height: normal;
	font-weight: bolder;
	color: #FFFBD4;
}
li {
	font-family: Verdana, Arial, Helvetica, sans-serif;
	font-size: 11px;
	line-height: normal;
	font-weight: normal;
	font-style: normal;
	color: #FFFBD4;
}
h1 {
	font-family: Verdana, Arial, Helvetica, sans-serif;
	font-size: 16px;
	line-height: 18px;
	font-weight: bolder;
	color: #FFFBD4;
}

a:link{
	color: #A1D9FF;
}

a:visited{
	color: #9C7AFF;
}

}
