css:
@-moz-document domain("twitchls.com") {
body, .highlight a, .highlight {
    background-color: #141D26!important;
    color: #ccc;
    text-decoration:none;
}

.c-input {
    color: #ccc;
}

.navbar-default, footer, .dropdown-menu, .card {
    background-color: #11131a;
}

.navbar {
    border-bottom: #1b1b1b 1px solid;
}
footer {
    border-top: #1b1b1b 1px solid;
}

.logo, .selected-tag, .dropdown-menu, .dropdown-menu > li > a {
    color: #8798A5!important;
}

.dropdown-toggle {border: 1px solid rgb(17, 19, 26);}

.btn-primary {
    color: #8798A5;
    background-color: #11131a;
    border-color: #17181b;
}

.btn-primary.focus, .btn-primary:focus, .btn-primary:hover, .btn-primary.active, .btn-primary:active, .open > .btn-primary.dropdown-toggle {
    color: #8798A5;
    background-color: #17181b;
    border-color: #17181b;
}


.form-control {
    color: #8798A5;
    background-color: #11131a;
    border: 1px solid #141d26;
}


.form-control:focus {
    border-color: #141d26;
}
}
