css:
@-moz-document regexp("https://eu.forums.blizzard.com/(.*?)") {
*{line-height:1.25 !important;}
.desktop-view .topic-post .topic-body{padding-top:16px !important;padding-bottom:16px !important;position:relative !important;}
@media (min-width: 480px) {body, p, li{font-size: 12px !important;}}
@media (min-width: 768px) {body, p, li{font-size: 13px !important;}}
@media (min-width: 992px) {body, p, li{font-size: 14px !important;}}
@media (min-width: 1200px) {body, p, li{font-size: 15px !important;}}
@media (min-width: 1900px) {body, p, li{font-size: 16px !important;}}
.desktop-view .topic-post .topic-avatar{padding-left:16px !important;margin-bottom:16px !important;}
.topic-avatar .main-avatar .avatar{margin-top:-20px !important;}
.desktop-view .topic-post .topic-body{padding-left:32px !important;}
.desktop-view .topic-post:nth-child(even) .row{background:rgba(255,255,255,0.05) !important;}
.desktop-view .topic-post:nth-child(odd) .row{background:rgba(255,255,255,0) !important;}
.topic-body .regular{margin-top:16px !important;}
}
