css:
@-moz-document url-prefix("https://sendleap.com/threads") {
.themed .color-secondary {
    background-color: #1e1e1edb;
}

.themed thread-view.active {
    background-color: #1e57dbfa;
}
/* Sidebar contact name font color */
.themed .color-text-secondary {
    color: #16ea65;
}

/* Sidebar snippet font color */
.themed .snippet {
    color: #ddec01;
}

/* Sidebar background color */
.threads {
    overflow-y: auto;
    animation: fadeIn .15s;
    background-color: #1e1e1edb;
}

/* Font color of details anme and number */
top-bar .details .name {
    color: #2f797d;
}

.themed .snippet.dark {
    color: #69e1e8;
}

/* Input box background and font color */
.themed send-box-input {
    background-color: #87f4b3e0;
}

send-box i.e {
    color: #db5307;
}


/* Text font color */
.themed .color-text-primary {
    color: #0f0f0f;
}

/* Sent messages bubble background color */
.themed message-bubble {
    background-color: #07a240;
}


}
