css:
@-moz-document domain("trello.com") {
/* font size (cards etc) */
    html, body, input, select, textarea {font-size: 15px !important;font-family:segoe ui;} /* instead of 14px */
    body {}

    /**************** HEADER *****************/

    /* header bar */
    #header {background: rgba(0, 0, 0, 0) !important;}

    /* header buttons */
    .header-btn.header-notifications, .header-btn.header-boards, 
    .header-btn.header-search, .header-btn.header-login, 
    .header-btn.header-signup {
        background: #ffffff45 !important;
        }
    .header-btn:hover {background: #ffffff9c !important;}

    /* header search box */
    #header-search input[type="text"] {background: #6B6B6B !important;}
    #header-search input[type="text"]:focus {background: #fff !important;}

    /* board title */
    #board-header .board-name {font-weight: normal !important;}

.board-header {
    margin-bottom: 0px !important;
}

.board-main-content > .board-header a, .board-main-content > .board-header p, .board-main-content > .board-header span {color:white !important;}
    /**************** BOARD*****************/
    


    /* board background */
    #board {
        background: rgba(0, 0, 0, .3) !important;
        padding-top: 20px;
        margin-bottom: 0px;
    }
    .body-custom-board-background #header ,.body-dark-board-background .board-header,.board-main-content > .board-header {
        zoom:1.2;
    background: rgba(0, 0, 0, .3) !important;
}

/* --------- scroolbar -----------*/
.list .u-fancy-scrollbar::-webkit-scrollbar{height:10px;width:3px;display:none;}
.u-fancy-scrollbar::-webkit-scrollbar-track-piece {background: #fff;}
.u-fancy-scrollbar::-webkit-scrollbar-thumb:vertical{background:#ddd;border-radius:2px;height:10px;}
    /**************** LISTS, CARDS *****************/

    /* list (container of cards) */
    .list {
        border: none !important;
        background: transparent !important;
        box-shadow: none !important;
        border-radius: 0 0 0 0 !important;
        }
    /* list title */
    .list-header {
/*         border-bottom: 2px solid #222 !important; */
/*         margin-left: 8px; */
        margin-right: 8px;
        padding-bottom: 12px;
    }
    .list-header-name {
        /* margin-left: 5px !important; */
        color: #fff !important; /* list text color */
        text-transform: uppercase !important; 
        font-size: 16px !important; 
        line-height: 18px !important;
        font-weight: 600 !important;
        letter-spacing: 1px !important;    
        }
.list-header-num-cards {color: #dcdcdc;display:initial;}
.list-header-extras-menu{color:white;border-radius: 55px !important;}
.list-header-extras-menu:hover {background: #ecf4f9;}


.list-wrapper:last-child {border-right: none ;}
    /* "Add a list" link */
    .list-wrapper.mod-add {
        transition: none;
        /* transition: background 50ms ease-in-out; */
    }
    .list-wrapper.mod-add.is-idle {
        width: 160px;
        margin-left:10px;
        background-color : rgba(0,0,0,.12);
    }
    .list.add-list.idle {
        background: none !important;
        box-shadow: none !important; 
    }
    
    .list-wrapper.mod-add.is-idle form {
    }
    .list-wrapper.mod-add.is-idle:hover {
        /* background: #eee; */
    }
    .list-wrapper.mod-add.is-idle .placeholder {
        text-transform: uppercase !important;
        font-size: 11px !important;
        letter-spacing: 1px;
        color: white;
        /*display: inline-block;*/
    }
	

    .list-cards {margin:0 10px;}
    /* CARD / item - default state */



    .list-card-quick-edit {
        padding: 0;
    }
    /* edit icon */
    .list-card-operation {
        background: #ddd;
        border-radius: 50%;
    }
    .list-card:first-child {    
    }
    /* card - on hover/selected */

    .list-card .card-operation {background-color: transparent !important;}/* hover action icon bg */

    /* card top and bottom divider */
    .list-gradient-top, .list-gradient-bottom {background: none !important;}
    /* labels */
    .card-label {
        border-radius: 0;
    }

    .list-card.placeholder {background-color:#0000001f !important; border:dashed 3px #00000036 !important; width:252px !important;}


    /* that eye icon*/
    .is-icon-only {display:none;}


    /* "Add a card" link*/
    .open-card-composer .icon-add
    {
      color:#ffffffa3;  
    }

    .open-card-composer {
        color:#ffffff9e;
        padding: 7px 0px 7px 16px;
        margin-left: 8px;
        margin-right: 8px;
        text-transform: uppercase;
        font-size: 11px;
        border-radius: 0;
        }
    .open-card-composer:hover {
        background: #eee !important; 
        color: #111 !important;
        border-radius: 20px;
        text-decoration: none !important;
        }

.card-label.mod-card-front {border-radius:5px;min-width:unset !important;}

    /**************** Colors *****************/
    .card-label-green {background-color: #2aca1f; }
    .card-labels-hover .card-label-green.mod-card-front, .card-label-green.mod-card-front  {background-color: #58f44d; }

    .card-label-yellow {background-color: #ffdf42; }
    .card-labels-hover .card-label-yellow.mod-card-front, .card-label-yellow.mod-card-front {background-color: #ffe460; }

    .card-label-orange {background-color: #ff9d57; }
    .card-labels-hover .card-label-orange.mod-card-front, .card-label-orange.mod-card-front {background-color: #fba466; }

    .card-label-red {background-color: #ff5a57; }
    .card-labels-hover .card-label-red.mod-card-front, .card-label-red.mod-card-front {background-color: #fd6a68; }

    .card-label-purple {background-color: #a857ff; }
    .card-labels-hover .card-label-purple.mod-card-front, .card-label-purple.mod-card-front {background-color: #bb7bff; }

    .card-label-blue {background-color: #577eff; }
    .card-labels-hover .card-label-blue.mod-card-front, .card-label-blue.mod-card-front {background-color: #6689ff; }

    .card-label-sky {background-color: #57ebff; }
    .card-labels-hover .card-label-sky.mod-card-front, .card-label-sky.mod-card-front {background-color: #6cedff; }

    .card-label-lime {background-color: #c7ff57; }
    .card-labels-hover .card-label-lime.mod-card-front, .card-label-lime.mod-card-front {background-color: #d1ff76; }

    .card-label-pink {background-color: #ff73e7; }
    .card-labels-hover .card-label-pink.mod-card-front, .card-label-pink.mod-card-front {background-color: #ff8eec; }

    .card-label-black {background-color: #4e4b49; }
    .card-labels-hover .card-label-black.mod-card-front, .card-label-black.mod-card-front {background-color: #7d7773; }

    .card-label-null {background-color: #d9d9d9; }
    .card-labels-hover .card-label-null.mod-card-front, .card-label-null.mod-card-front {background-color: #e7e7e7; }

    .card-label.mod-square,.card-label.mod-card-detail {border-radius:30px;}

    
    /**************** CARD CONTENT (popup window) *****************/

    /* title */
    textarea.mod-card-back-title {font-size:24px !important;
    height:32px
    }

    /* popup window */
    .window {
        border-radius:5px;
        background-color: #f7f7f7;
    }
    /* labels */
    .card-label {text-shadow: none !important;}

    /* card content buttons */
    .button-link:hover {
        background: -moz-linear-gradient(center top , #676767 0%, #3F3F3F 100%) repeat scroll 0 0 transparent !important;
        border-color: #3F3F3F !important;
        }

    /* More links */
    .pop-over-list li > a:hover,
    .uploader:hover .fakefile {background: #424242 !important; border:none !important;}

    /* button for comment etc */
    input.primary[type="submit"], input.primary[type="button"], 
    button.primary, .button.primary {
        background: -moz-linear-gradient(center top , #676767 0%, #3F3F3F 100%) repeat scroll 0 0 transparent !important; }

    /* image/attachment icon and background */
    .attachment-list li .preview .check-icon {background-color: #676767 !important;}
    .attachment-list li .preview.is-cover {background-color: #676767 !important;}

    /**************** TOGGLE BAR *****************/

    /* toggle arrow on the right */
    .extra-large-window .board-wrapper.disabled-all-widgets .board-side-btn.right-side .arrow, 
    .large-window .board-wrapper.disabled-all-widgets .board-side-btn.right-side .arrow, 
    .medium-window .board-wrapper.disabled-all-widgets .board-side-btn.right-side .arrow {
        border-right-color: #757575 !important;} /* open arrow */ 
    .extra-large-window .board-side-btn.right-side .arrow, 
    .large-window .board-side-btn.right-side .arrow, 
    .medium-window .board-side-btn.right-side .arrow {
        border-left-color: #757575 !important;} /* close arrow */ 
    /* toggle bar */
    .extra-large-window .board-side-btn:hover, 
    .large-window .board-side-btn:hover, 
    .medium-window .board-side-btn:hover {background: #ADADAD !important;}

    /* side menu */
    .board-menu {
        background-color: #f7f7f7;
    }

    /* end */


/* ####################################################################
#################################################################### */


.board-header-btn-filter-indicator .board-header-btn-icon-close {background: #ffffff40;}
.board-header-btn-filter-indicator {background: #ffffff70;}
.classic-body {background-color: blue !important;background-image:none !important;}


.list-card-details {padding:10px 8px 4px 8px;}

/*
,.list-card-labels
.list-card-members

*/
span.js-badges > div[title="You are watching this card."] {display:none;}

.badges .js-badges .badge[title="Comments"] {display:none !important;}
.badges .js-badges .badge.is-due-future {display:none !important;}
.badges .js-badges .badge[title="Attachments"] {display:none !important;}

.badge.is-due-complete {background-color:#61bd4f00; color:gray;}
.badge.is-due-complete .badge-icon {color:gray;}

.list-wrapper {border-right: 1px solid #ffffff1c; border-right-width:0px; margin:0; width:300px;}


.board-header-btn.mod-show-menu.js-show-sidebar {padding-left:10px;}

.board-header-btn-filter-indicator:hover {background: #ffffffa6!important;}
.board-header-btn-filter-indicator:hover .board-header-btn-icon-close {background: #ffffff59;}

.card-label.mod-card-front {max-width: 8px; border-radius: 20px; font-weight: 600; display:none;}

.body-card-label-text-off .card-label.mod-card-front ,.body-card-label-text .card-label.mod-card-front {display:none;} 
.body-card-label-text-on .card-label.mod-card-front , .body-card-label-text .card-label.mod-card-front {display:inline !important;}

.list-card-members .member , .list-card-members .member .member-avatar {width:20px; height:20px; }


.board-header-btn.board-header-btn-org-name.js-open-org-menu.board-header-btn-without-icon, .board-header-btn-divider,
.board-header-btn.perms-btn.js-change-vis, .icon-sm.icon-overflow-menu-horizontal.board-header-btn-icon {display:none;}
}

@-moz-document domain("trello.com") {
/*
*/
.js-board-header {display:none;}


.list-card {
     border-radius: 5px !important;
     padding: 6px 8px 5px;
     margin-bottom: 10px;
     box-shadow:none;
     background-color:#ecf4f9;
     background-color:#f0f0f0;
     background-color: white !important;
     border: solid #fff 3px !important;  
   /*
   */  
}

.list-card:hover {transition:background-color,border .1s ease-in-out;}

.list-card.selected, .list-card.active-card {      
    background: #fffaed !important;     /* card background color on hover */
    border-color: #ffcd43 !important;   /* same as background color (bottom-border) */
}

/*
.list-header-num-cards.js-num-cards {background-color:#51e651;color:#fff;border-radius:10px;
   text-transform:uppercase; font-size:12px; font-family:Arial; letter-spacing:1px; padding:0px 8px;margin-top:0px;}
*/

.list-header-num-cards.js-num-cards {display:none;}


.calendar-week .calendar-day {background: #f4f4f4; }
.calendar-week .calendar-day.today {background-color: #d1ffc9;}
}

@-moz-document url("https://trello.com/b/bUSEp1e7/product-design") {
/*
.list-card {border: none !important;}

.list-wrapper {width:300px;}
.list-wrapper .list-header-num-cards.js-num-cards {display:none;}
.badges {display:none;}
.list-card-members {display:none;}
*/

.list-wrapper:nth-last-child(4) {width:350px; margin: 0 20px;}
.list-header-name.mod-list-name.js-list-name-input {height:30px !important;}


.list-wrapper:nth-child(1) ,.list-wrapper:nth-child(2), .list-wrapper:nth-last-child(3) , .list-wrapper:nth-last-child(2) { opacity:.5;}
.list-wrapper:hover {opacity:1;}
.list-wrapper:nth-last-child(2) .list-card-title {text-decoration:line-through;}


.list-wrapper:nth-child(3) .list-card-members {display:block;}
.list-wrapper:nth-last-child(2) .badges, .list-wrapper:nth-last-child(2) .list-card-labels.js-card-labels, .list-wrapper:nth-last-child(2) .list-card-members {display:none;}


.list-card {
padding: 0px 8px 0px;
margin-bottom: 10px;
background-color: #f9f9f9 !important;
border-radius:3px !important;
box-shadow: none;
}
/*
border: 1px solid #e5e5e5 !important; 
box-shadow: 0px 1px 3px 0px #08145211 !important;   
*/
}

@-moz-document url-prefix("https://trello.com/b/d8CSYbnU/tasks") {
.list-wrapper:nth-child(3) .list-header-num-cards.js-num-cards {background-color:#ec6d1f;}
.list-wrapper:nth-child(4) .list-header-num-cards.js-num-cards {background-color:#ffdb00;}
.list-wrapper:nth-child(5) .list-header-num-cards.js-num-cards {background-color:#1fec3b;}

.list-wrapper:nth-child(1) .list-header-num-cards.js-num-cards, .list-wrapper:nth-child(2) .list-header-num-cards.js-num-cards, .list-wrapper:nth-child(4) .list-header-num-cards.js-num-cards,
.list-wrapper:nth-last-child(2) .list-header-num-cards.js-num-cards
{background-color:white; color:#d0d0d0; padding-left:0px;}
}
