css:
@-moz-document domain("app.dnsfilter.com")
{
body {
    color: #000;
    font-weight: 500;
}
.table {
    color: #000;
}
.Select, .Select div, .Select input, .Select span {
    color: #000;
}
.label-default {
    color: #000;
}
.site-menu-item a {
    color: #fff;
}
.site-menu .site-menu-sub .site-menu-item.active > a {
	color: #fff;
    font-weight: bold;
}
.btn-default, .form-control, .pt-input, .pt-control, .pt-label {
    color: #000;
    font-weight: 500;
}
.nav-tabs > li > a {
	color: #000;
}
.table > tfoot > tr > th, .table > thead > tr > th {
	color: #000;
}
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
	color: #000;
}
table.light-table tfoot tr th, table.light-table thead tr th, table.light-table tbody tr th {
	color: #000;
}
}
