css:
@-moz-document url("https://processing.org/reference/"), url("https://www.processing.org/reference/") {
div.ref-col {
    width: auto;
}

.ref-col h5 {
    border-radius: 2px;
    border-bottom: 2px solid lightgray;
    line-height: 1em;
}

.category {
    padding: 2px 16px 12px;
    background: whitesmoke;
    border: 2px solid gray;
    border-radius: 9px;
    box-shadow: inset 0 2em 0 0 antiquewhite;
}

.ref-col a.ref-link {
    display: inline-block;
    margin: 2.5px;
    background: aliceblue;
    border: 1px solid lightsteelblue;
    border-radius: 3px;
}

.category b {
    position: relative;
    display: block;
    font-size: 120%;
    line-height: 150%;
    padding-left: 2px;
}

.category br {
    display: block;
    content: "" !important;
    height: 0.75em;
}
}
