css:
@-moz-document regexp(".*") {

@keyframes wew {
    from {filter: hue-rotate(0deg);}
    to {filter: hue-rotate(360deg);}
}

@keyframes wew2 {
    0% {transform: scale(1, 1) rotate(0deg);}
    12.5% {transform: scale(1.1, 1) rotate(1deg);}
    25% {transform: scale(1, 1) rotate(0deg);}
    37.5% {transform: scale(1, 1) rotate(-1deg);}
    50% {transform: scale(1.1, 1) rotate(0deg);}
    62.5% {transform: scale(1, 1) rotate(1deg);}
    75% {transform: scale(1, 1) rotate(0deg);}
    87.5% {transform: scale(1.1, 1) rotate(-1deg);}
    100% {transform: scale(1, 1) rotate(0deg);}
}

@keyframes wew3 {
    0% {filter: invert(0);}
    50% {filter: invert(1);}
    100% {filter: invert(0);}
}

@keyframes wew4 {
    0% {font-size: 12px;}
    50% {font-size: 24px;}
    100% {font-size: 12px;}
}

@keyframes wew5 {
    0% {letter-spacing: 1px;}
    25% {letter-spacing: 3px;}
    50% {letter-spacing: 1px;}
     75% {letter-spacing: 3px;}
    100% {letter-spacing: 1px;}
}

@keyframes wew6 {
    0% {size: 100%;}
    50% {size: 150%;}
    100% {size: 100%;}
}

@keyframes wew7 {
    0% {background-size: 100%;}
    50% {background-size: 150%;}
    100% {background-size: 100%;}
}

p, h1, h2, h3, h4, h5, h6, a, span {
    animation-name: wew3, wew4, wew5;
    animation-duration: 5s;
    animation-timing-function: linear;
    animation-iteration-count: infinite;
}

img {
    animation-name: wew6;
    animation-duration: 10s;
    animation-iteration-count: infinite;
}

div {
     animation-name: wew7;
    animation-duration: 10s;
    animation-iteration-count: infinite;
}

body {
    animation-name: wew, wew2;
    animation-duration: 10s;
    animation-timing-function: linear;
    animation-iteration-count: infinite;
}
/* i really want this to be global */
}
