css:
@-moz-document url-prefix('https://dlm.carsforsale.com/forum/index.aspx') {
	.content {
		max-width: 100%;
	}

	.demo-half {
		width: 50% !important;
	}

	.demo-footer {
		display: block;
		margin: 0 20px;
	}

	.demo-textarea {
		height: 700px;
	}

	#comb {
		display: block;
		margin: 0 auto;
		width: 100%;
		padding: 20px 0;
		background-color: #e74c3c;
		color: #fff;
		transition: 200ms;
	}

	#comb:hover {
		background-color: #fff;
		color: #e74c3c;
	}
}
