css:
@-moz-document domain('canjs.com') {
body,
#right,
.brand {
    color: #aaa;
    background-color: #0e0e0e;
}

#left {
    background-color: #14171b
}

h1, h2, h3, h4, h5, h6 {
    border-color: #777;
}

h3 {
    color: #ccc;
}

p {
    color: #aaa;
}

.brand .logo > a {
    background: #777;
    -webkit-mask: url(https://canjs.com/doc/static/node_modules/bit-docs-html-canjs/static/img/canjs_logo.svg) no-repeat 50% 50%;
}

.brand .version .version-number::after {
    background: #777;
    -webkit-mask: url(https://canjs.com/doc/static/node_modules/bit-docs-html-canjs/static/img/down_black.svg) no-repeat 50% 50%;
}

#left > .bottom ul li span {
    color: #777;
}

#left > .bottom .nav-menu > ul > li,
#left > .bottom .nav-menu > ul > li > ul > li > ul {
    border-color: #777;
}

.search-bar,
.search-bar .search-wrap .search {
    /* match to .breadcrumb */
    background-color: #272e35;
}

.search-bar .search-wrap .search::-webkit-input-placeholder {
    color: #777;
}

.hero-images {
    border-color: #777;
}

article .signature {
    background-color: #1c222b;
    border-color: #555;
}

article .codepen,
pre code .collapse.collapsed {
    background-color: #111;
    border-color: #777;
}

article .codepen:hover {
    color: #aaa;
    background-color: #222;
}

article pre[class*=language-] > code {
    box-shadow: 0 0 0 1px #777;
}

code[class*=language-], 
pre[class*=language-] {
    color: #aaa;
}

:not(pre) > code[class*=language-], 
pre[class*=language-],
pre[class*=language-] > code{
    background-color: #222;
}

:not(pre) > code {
    color: #ff5050;
}

.token.entity, 
.token.operator, 
.token.url, 
.token.variable {
    background-color: rgba(37, 37, 37, 0.5)
}

@media screen and (min-width: 1000px) {
    .breadcrumb {
        /* match to .search-bar */
        background-color: #272e35;
    }
    
    .breadcrumb li a {
        color: #aaa;
    }
    
    .breadcrumb li a:hover {
        background-color: transparent;
    }
    
    .breadcrumb .breadcrumb-dropdown:hover a,
    .breadcrumb .breadcrumb-dropdown:hover ul {
        background-color: #1c222b;
    }
}

}
