css:
@-moz-document domain("onlinetimer.ru") {
body {
    background: #282B2F;
}
#body {
	color: #e3e3e3;
    background: #282B2F;
    box-shadow: none;
    border: 1px solid #181B1F;
}
.timer-display input[type="text"] {
    color: #0099dc;
    text-shadow: none;
	background: #282B2F;
}
.timer .description input {
	background: #282B2F;
    color: #999;
}
.setup-timer {
    color: #e3e3e3;
}
select {
    color: #e3e3e3;
    background: #282B2F;
}
input.small {
    color: #e3e3e3;
    background: #282B2F;
}
a {
    color: #999;
}
.times-setup {
    border-top: 2px solid #181B1F;
    color: #e3e3e3;
    background: #494d50;
}
.my-btn {
    color: #e3e3e3;
    background: #494d50;
}
dd {
    color: #e3e3e3;
}
input, textarea, select, button {
    border-color: #181B1F;
	color: #e3e3e3;
    background: #373B3E;
}
.my-info-box {
    color: #e3e3e3;
    background: #373B3E;
    text-shadow: none;
}
.my-error {
	color: #e3e3e3;
    background-color: #494d50;
}
}
