css:
@-moz-document domain("www.viki.com") {
.navbar ul, .container .row , .select2-container .select2-choice>.select2-chosen, .page-wrapper, .dropdown-section, .navbar-primary, .info-tooltip, .hover-tooltip .hover-tooltip-text, .alert-info, .alert-success, .rating-placeholder, .rating-placeholder .content-link {
    background-color: black;
    color: white;
}

.navbar-dropdown-item .navbar-dropdown-container, .navbar-dropdown-item .navbar-dropdown-container a:hover, .modal-content{
	background-color: black;
}

.navbar-dropdown-item .navbar-dropdown-container a:hover{
	background-color: darkblue;
}

.login-tab .social-login{
    background-color: darkslategrey;
}

.navbar-secondary .container ul{
	background-color: #1d8bdd;
}

.navbar-secondary{
	background: #1d8bdd;
}

.thumb-caption, .thumb-title, .billboard .billboard-meta .display-2, .emphasis, .content-heading, .card-heading, .section-heading, .form-field label, .more-info-link, .btn-favorite, .navbar-expanded a, .content-link{
	color: white;
}

.thumb-caption, .thumb-title:hover, .rating-placeholder .content-link:hover{
	color: lightyellow;
}
.text-truncate:after {
    background-image: none !important;
}
.card {
    background-color: black;
}

.page-footer .page-footer-section{
	background-color: darkblue;
}

.review-rating{
	color: lightgray;
}
}
