css:
@-moz-document domain('www.resetera.com') {
.structItem-cell--icon {
    display: none;
}

article.message .message-cell--user img[itemprop="image"] {
    display: none;
}

article.message .avatar {
    display: none;
}

article.message .message-avatar {
    display: none;
}

article.message .uix_messagePostBitWrapper {
    position: absolute;
    left: 100%;
    width: 180px;
    overflow: hidden;
    padding: 10px 2px;
}

article.message .message-userDetails {
    text-align: left !important;
    margin-left: 10px;
    white-space: nowrap;
}
article.message .message-userTitle {
    display: inline-block;
    display: none;
}

article.message .message-name {
    text-align: left;
    display: inline-block;
}

article.message .message-userBanner {
    text-align: left;
    display: inline-block;
}

article.message .message-attribution {
    margin-left: 180px;
    position: relative;
}

article.message .message-cell--user {
    padding: 0;
    width: 0;
    height: 0;
    min-width: 0;
    min-height: 0;
    flex: 0 0 0px;
}
}
