css:
@-moz-document domain(tweetdeck.twitter.com){

/* Layout */

#container > .horizontal-flow-container
{
 width: 100%;
 max-width: 1400px;
 position: relative;
 left: 50%;
 transform: translateX(-50%);
}
#container > .horizontal-flow-container > section:nth-child(1)
{
 position: absolute;
 top: 0;
 height: 49%;
}
#container > .horizontal-flow-container > section:nth-child(2)
{
 height: 50%;
}
#container > .horizontal-flow-container > section:nth-child(3)
{
 width: 46.5%;
 min-width: 300px;
 max-width: 650px;
}

/* Bottom Borders */
.column-header, .column-header-temp
{
 border-bottom-color: #0a2131;
}
.search-results-container .stream-item,
.stream-item
{
 border-bottom-color: #0a1020;
}
.js-column-options > .with-column-divider-bottom,
.column-options,
.facet-type
{
 border-bottom-color: #071426;
}
.facet-type:last-child
{
 border-bottom: 1px solid #071426;
}
.dark-border,
.detail-view-inline,
.mdl-header-divider,
.tweet-detail-actions
{
 border: 0;
}
.detail-view-inline-text
{
 border-color: #071426;
}

/* Colors */

.app-columns-container, .app-content
{
 background-color: #0C1923;
 background-blend-mode: screen;
}
.column-panel,
.scroll-conversation,
.column-message
{
 background-color: #0D1D26;
}
.bg-color-twitter-white
{
 background-color: #0D1D26 !important;
}
.search-results-container .stream-item,
.stream-item
{
 background-color: #132938;
}
.column-options .button-tray,
.column-options
{
 background-color: #0C2436;
}
.facet-type.is-active,
.detail-view-inline-text
{
 background-color: #11334C;
}
.column-header,
.column-header-temp
{
 background-color: #1a3141;
}

div[data-testid="legacyCard"]
{
 background: #eee;
}

.mdl,
.inline-reply,
.compose,
.popover
{
 background-color: #21435C;
}
.js-search-in-popover .caret-inner
{
 border-bottom-color: #21435C;
}
.attach-compose-buttons .tweet-button
{
 background-color: #21435C !important;
}
.mdl > .mdl-inner,
.compose-text-container,
.btn-on-blue
{
 background-color: #31536C;
}
.js-search-in-popover input,
.search-input-control input,
input, select, textarea,
textarea.bg-color-twitter-white,
.mdl-column-med,
.js-stream-item-content.item-box > .js-detail-view-inline.dark-border
{
 background: transparent !important;
}
.column-title-container:hover .column-title-items
{
 border-bottom-color: #fff;
}


html,
.lst-launcher a span,
.column-title-container,
.account-link
{
 color: #cbe4f6;
}

.txt-mute,
.quoted-tweet,
.account-summary .dropdown-menu a:not(:hover):not(:focus),
.tweet-action-item.position-rel .dropdown-menu a:not(:hover):not(:focus),
.tweet-detail-action-item.position-rel .dropdown-menu a:not(:hover):not(:focus),
.js-search-in-popover .list-item
{
 color: #8bb4c6;
}

.column-heading,
.accordion .is-active .accordion-header,
.accordion .is-active .accordion-header:hover,
.compose-text,
.column-title-back,
.js-search-in-popover .list-item:hover,
.js-ticker-value
{
 color: #fff;
}

}
