css:
@-moz-document domain("quicktopic.com") {
body
{
    background-color: black;
}

a, a:visited
{
    color: #88e;
}

a:hover
{
    color: #ff0;
}

.custom-contrast-color
{
    color: #eee;
}

.heading-text-color
{
    color: #eee;
}

.topic-messageauthorcontainer-border
{
    border-top-color: #448;
    border-right-color: black;
    border-left-color: #336;
    border-bottom-color: #111;
}

.div-post-username
{
    color: #ccf;
}

.topicname
{
    color: #cce;
}

.textarea-compose-message
{
    background-color: #116;
    color: #eee;
}

.messagerow:nth-child(even)
{
    background-color: #224;
    color: #eee;
}

.messagerow:nth-child(odd)
{
    background-color: #336;
    color: #eee;
}

.messageauthor
{
    color: #8cf;
}

.messagedata
{
    color: #fff;
}
}
