css:
@-moz-document domain("startpage.com") {
.theme--dark header {
    padding: 10px 20px 0 !important;
}

.theme--dark .header__content {
    margin: 10px 0 !important;
}

.theme--dark header,
.theme--dark body,
.theme--dark footer,
.theme--dark main,
.theme--dark .search-options__bar,
.theme--dark .settings-page__form-action,
.theme--dark .burger-menu__content,
.theme--dark section,
.theme--dark .home-footer__bottom,
.theme--dark .hero-search__learn-more {
    background-color: #333333 !important;
}

.theme--dark .curve--top::before,
.theme--dark .curve--top::after,
.theme--dark .curve--bottom::before,
.theme--dark .curve--bottom::after {
    background-color: #4b4b4b !important;
}

.theme--dark .burger-menu__overlay {
    background-color: #292929 !important;
    opacity: 0.8 !important;
}

.theme--dark .search-form__input {
    background-color: #292929 !important;
    border: none !important;
}

.theme--dark .search-form {
    box-shadow: none !important;
}

.theme--dark .search-item__title a,
.theme--dark .search-item__url,
.theme--dark .search-item__title {
    color: #e6e6e6 !important;
}

.theme--dark .search-item__url {
    font-size: 12px !important;
    opacity: 0.6 !important;
    padding: 0 0 5px 0 !important;
}

.theme--dark li.search-result {
    margin-bottom: 30px !important;
}

.theme--dark .spell-suggestion,
.theme--dark .instant-wiki,
.theme--dark .card,
.theme--dark .search-suggestions__list {
    background-color: #4b4b4b !important;
    box-shadow: none !important;
    margin: 0 0 20px 0 !important;
    border: 1px solid #4b4b4b !important;
}

.theme--dark .search-item__category,
.theme--dark .search-item__sub-title {
    color: var(--brand-blue-light) !important;
}

.theme--dark .promotion {
    display: none !important;
}

.theme--dark .column--left {
    width: 0 !important;
    padding: 0 !important;
}

.theme--dark .column--main {
    padding-left: 20px !important;
}
}
