css:
@-moz-document domain(forum.heavyelement.io) {
	
body {
    background: #111;
    color: #c1c1c1;
}

.DiscussionListItem:hover {
    background: #272727;
}

.Dropdown-menu {
    background: rgb(31, 31, 31);
}

icon, .Dropdown-menu .Button-label {
    color: #c1c1c1;
}

.Header-logo {
    filter: grayscale(100%) brightness(500%);
}

.App-header {
    background-image: url(https://i.imgur.com/fXTximC.png);
    filter: blur(50%);
}

.Dropdown-menu>li>a:hover, .Dropdown-menu>li>button:hover {
    background: #111;
}

.Button {
    color: #998466;
    background: #272727;
}

.Button:hover, .Button:focus, .Button.focus {
    background-color: #000000fa;
}

.Button.active, .open>.Dropdown-toggle.Button {
    background-color: #000000fa;
}

.Dropdown-menu .Button-icon, .Dropdown-menu .Button-label {
    color: #7c7c7c;
}

.Post-body blockquote {
    background: #272727;
    border-top: 2px dotted #5a5a5a;
    border-bottom: 2px dotted #5a5a5a;
}

.DiscussionListItem-title {
    color: #d77355;
}

.Dropdown-menu>li.active>a, .Dropdown-menu>li.active>button {
    background: #111;
}

.unread .DiscussionListItem-count {
    color: #7c7c7c;
}

a {
    color: #b72a2a;
}

.NotificationGrid {
    background: #272727;
}

h3 a {
    color: #7c7c7c !important;
}

.Composer {
    background: #111;
}

.Composer.active {
    background: #111;

}

.TextEditor textarea {
    color: #c1c1c1 !important;
}

.TagDiscussionModal .Modal-header {
    background: #111;
    padding: 20px 20px 0;
}

.Modal-content {
    background-color: #272727;
}

.Modal-body {
    background-color: #111;
}

.Modal-body .FormControl {
    background-color: #272727;
    color: #111;
}

.ComposerBody-header h3 input, .ComposerBody-header h3 input[disabled], .ComposerBody-header h3 input:focus {
    color: #d77355;
}

.SelectTagList>li.active {
    background: rgba(0,0,0,0.35);
}

.Dropdown-header {
    color: #c1c1c1;
}

.darkenBackground {
    background: rgba(0,0,0,0.9) !important;
}

.App-content {
    background: #111;
}

.DiscussionPage-list {
    background: #111;
}

.DiscussionPage-list .DiscussionListItem.active {
    background: #000000fa;
}

.Scrubber-handle {
    background: #111;
}

.Scrubber-before, .Scrubber-after {
    border-left: 1px solid #272727
}

.disabled .Scrubber-bar {
    background: #272727;
}

.Dropdown-menu {
    text-align: left;
    color: #c1c1c1;
}
.Hero {
    background: rgba(0,0,0,0.9) !important;
}

}
