css:
@-moz-document domain("forums.ebay.com.au")

{

/* Change background colour */
body { background: #adbdc9 !important; }

/* Disable topic pop-ups on topics pages */
.thread-snippet {display : none !important;}

/* Remove right side column */
.sidebar, #sidebar2 { display : none !important;}
#sidebar-column { display : none !important;}

/* Remove all avatars from topics pages and posts */
.lw-message-author-photo-avatar, .lw-photo img, .lw-author-photo-bezel img, .lw-author-photo-frame td {display : none !important;}

/* Remove 'View Listing' link line on posting IDs */
.view_list  { display : none !important;}

/* Change font size for topic titles */
h3 div.lw-featured-post-excerpt, .lw-value h3, .lw-text h3, .lw-forum-table h2 {font-size : 12px !important;}

/* Change font size of text in posts */
.lw-thread-body-message { font-size : 14px !important;}

}
