css:
@-moz-document url-prefix("https://openid.net/specs/") {
  body {
    background-color: #404245;
    margin: auto;
    width: 960px;
  }
  a:link, h1 {
    color: #f99;
  }
  a:visited {
    color: #e55;
  }
  body, h3 {
    color: #ddd;
  }
  tt {
    color: #9cf;
    font-weight: bold;
  }
  pre {
    background-color: #000;
    color: #fff;
  }
  body, tt {
    font-size: 16px;
    line-height: 22px;
  }
}
