css:
@-moz-document url-prefix('https://www.google.') {
#searchform form div.logo + div[jsname][class] {
    background: #fff;
	display: flex;
	border-radius: 2px;
	border: none;
	box-shadow: 0 2px 2px 0 rgba(0,0,0,0.16), 0 0 0 1px rgba(0,0,0,0.08);
	height: 39px;
	width: 632px;
	z-index: 3;
	height: 44px;
	margin: 0 auto;
}

#searchform.minidiv form div.logo + div[jsname][class] {
    height: 36px  !important;
    margin: 7px auto !important;
}



#searchform form div.logo + div[jsname][class] + div[class][jsname] > div + div[class][jsname] {
    border-radius: 4px;
    box-shadow: none;
    border: 1px solid #dadada;
    border-top: none;
    border-top-left-radius: 0;
	border-top-right-radius: 0;
    margin: -1px;
}

#searchform.minidiv form div.logo + div[jsname][class] + div[class][jsname] > div + div[class][jsname] {
   margin-top: -8px !important; 
}

div[role="navigation"] > div, #searchform div.nojsv[style] {
   	background: #fafafa !important
}
}
