css:
@-moz-document url-prefix(https://store.steampowered.com/app/) {
#application_root
{
    position:relative;
    top:980px;
}
#game_highlights
{
	position:relative;
}
#game_highlights > .rightcol
{
	width: 100%;
    margin-left: 0;
    padding-top: 980px;
}
#game_highlights > .rightcol > .glance_ctn > .game_header_image_ctn
{
	float: left;
    width: 50%;
	padding: 0 16px 0 0;
	box-sizing:border-box;
}
#game_highlights > .rightcol > .glance_ctn > .game_header_image_ctn > img
{
	width:100%;
	height:auto;
}
#game_highlights > .rightcol > .glance_ctn > .game_description_snippet,
#game_highlights > .rightcol > .glance_ctn > .glance_ctn_responsive_left,
#game_highlights > .rightcol > .glance_ctn > .glance_ctn_responsive_right
{
	float: right;
    width: 50%;
    box-sizing: border-box;
    margin: 0;
    padding: 0 16px 0 0;
}
#game_highlights > .leftcol
{
	position: absolute;
	width: 150%;
    top: 0;
    left: -25%;
	background: rgb( 20, 30, 40);
	box-sizing: border-box;
    padding: 7px;
    box-shadow: 0 0 100px #000;
}
#game_highlights > .leftcol .highlight_overflow
{
	margin:0;
}
.highlight_player_item.tall img
{
	width:100%;
}
.queue_overflow_ctn
{
	position: absolute;
    top: 1153px;
    margin: 0 auto;
    width: 940px;
    left: 50%;
    margin-left: -470px;
}

.broadcast_embed_hideable
{
	    position: relative;
    top: 990px;
}
}
