css:
@-moz-document domain(inoreader.com) {
* {
    text-shadow:none !important;
}
html, body, #wraper, #sitecontent, #splitter {
    height:100% !important;
}
#consent_xmas2018,
iframe {
    display: none;
}
#subscriptions_buttons {
    position: absolute;
    top: 0;
    right: 0;
    left: 0;
    margin-top: -32px;
    transition: margin 250ms 250ms;
}
#subscriptions_buttons:hover {
    margin-top: 0;
    transition: margin 250ms;
}
#splitter {
    top: 0px;
}
#tree_pane,
#reader_pane,
#three_way_contents {
    height: calc(100% - 8px) !important;
    top: 8px;
}

#three_way_contents .article_full_contents {
    padding-top: 60px !important;
}
.reader_pane_view_style_2 .article_stripe {
    width: 4px;
    top: 0px;
    left: 0px;
    bottom: 0px;
    height: auto !important;
}
.article_unreaded .article_stripe {
    background-color: steelblue;
}
.article_content {
    max-width: 710px;
}
.article_content img {
    opacity: 1;
    width: 100%;
}
.column_view_title {
    font-size: 13px;
}
.column_view_title span {
    font-weight: 100;
}
.article_footer_3way {
    top: 3px !important;
}
}
