css:
@-moz-document domain("10fastfingers.com") {

#words span {
    color: transparent !important;
}

#words .wrong, #words .correct {
    color: transparent !important;
}

#words .highlight, #words .highlight-wrong {
    color: #111111 !important;
    background-color: transparent !important;
}
 
#words .highlight + span, #words .highlight-wrong + span {
    color: #111111 !important;
}

#words .highlight + span + span, #words .highlight-wrong + span + span {
    color: #111111 !important;
}

#words .highlight + span + span + span,  #words .highlight-wrong + span + span + span {
    color: #111111 !important;
}

input#inputfield.form-control {
    color: transparent !important;
}

}
