css:
@-moz-document domain("www.rabb.it") {
#rabbitapp { background: #171c23 }
#rabbitapp>.content { margin-top: 0 !important; height: 100% !important; max-width: calc(100% - 340px) !important; }
#rabbitapp>.social { top: 0 !important; }
.tray { margin-bottom: -88px; transition: margin-bottom 0.1s 0.3s ease-in; }
.tray.screencast { background: #1f242e !important; }
.tray:hover { margin-bottom: 0; }
.trayView, .bubblesContainer { height: 90px !important; }
.controls { height: 32px; }
.banner { display: none !important; }
.roomVisibilityPopup { display: none !important; }
.joinRoomNagView { display: none !important; }
.featureInvitePopupView { display: none !important; }
.roomFeaturedStatus { display: none !important; }
body.bannerVisible #rabbitapp:not(.toolbarVisible)>.social { top: 0 !important; }
body.bannerVisible #rabbitapp:not(.toolbarVisible)>.content { margin-top: 0 !important; height: 100% !important; }
.invitePeople, .roomUrl { display: none !important; }
.fullscreenButton, .controlContainer.bigPicture { opacity: 0.3; }
.toolbarVisible .miniToolbarView { opacity: 1 !important; max-width: 300px !important }
.toolbar { display: none; }
.overlayBackground.overlayModal { display: none; }
.interestPickerView { display: none; }
.roomInvitePromptView { display: none; }
div.messageInput { margin: 0 !important }
textarea.conversationInput { height: 50px !important; width: 330px !important; }
.chatsNavigation .roomName, .chatsNavigation .roomInterestName { height: 28px !important; line-height: 32px !important; }
.chatLayout .header .roomHeaderLayout .back .icon svg { margin: 6px !important; }
.chatLayout .header { height: 62px !important }
}
