css:
@-moz-document domain("lhtranslation.net") {
#page {
    background-color: #232222;
}
.main-container {
    background-color: #272727;
}
#logo a {
    color: rgb(255, 87, 87);
}
b,
strong {
    color: #ff3c3c;
}
element.style {
    color: #fff;
}
.post.excerpt {
    background-color: #353434;
    border: 1px solid #3e3939;
}
body {
    color: #d6d6d6;
}
.post-info a,
.single-post .post-info a {
    color: #ff6363;
}
.single_post {
    background-color: #313030;
    border: 1px solid #460202;
}
td, th {
border: 1px solid #311414;
}
.primary-navigation, footer, .readMore a:hover, #commentform input#submit:hover, .featured-thumbnail .latestPost-review-wrapper {
    background-color: #563636;
}
.title a {
    color: #ff2929;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #ff3c3c;
}
.sidebar.c-4-12 a {
    color: #dedede;
}
#s,
#woocommerce-product-search-field {
    background-color: #272525;
}
input,
textarea,
select {
    color: #ffffff;
    background-color: #232222;
}
pre {
    background: #00000042;
}
}
