css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("digg.com") {

#container { max-width:1050px !important; }
.main { padding: 15px 30px 15px 15px !important; }

.customize-login, #h-upcoming,
.selector, .side-options, .topten, #announce, .join-digg,
.digg-it, .news-details, .topic,
.add-widget, #recommended-list, .promo,
.what-is-digg, .user-info, .loginFBButton, #top-submit, #footer,
.newsletter
{ display:none !important; }

.sidebar .side-container+.side-container { display:none !important; }

.news-summary a + em { visibility:hidden !important; }
.news-summary:hover a + em{ visibility:visible !important; }

.news-summary:hover .news-details,
.news-summary:hover .digg-it
{ display:block !important; } 


#h-pri, #h-sec, .header-join, .header-about, .header-login
{ display:none !important; }
#h {height:45px !important; -moz-border-radius:0 0 5px 5px !important;}

.main .news-summary {
margin-left:10px !important;}

.side-container .news-summary, .p-archive .news-summary {
border-top:none !important;
padding-bottom:8px !important;
}

.news-summary h3 a { background:none !important; } 

}
