css:
@-moz-document domain('lex-kravetski.livejournal.com') {

body, .entry-content, .comment-text {
    font-family: Calibri !important;
}

.entry-content, .comment-text, .sidebar-block dd, .comments-links {
    font-size: 10.5pt !important;
    line-height: 13pt;
}

.comment-upic {
    display: none !important;
}

.comment-head-in {
    margin-left: inherit;
}

.comment-metadata, .comment-poster-info {
    display: inline;
}

a:visited, a:link {
    border-bottom: none !important;
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}

.entry-content br, .comment-text br {
    line-height: 10pt;
}

.comment-menu, .comment-datetimelink {
    font-size: 9pt;
}

.comment-metadata {
    margin-bottom: 0;
    margin-top: 0;
}

dl.vcard {
    display: none;
}

div.lj-like, .entry-linkbar {
    opacity: 0.3;
}

.content-inner, #page, #page .btn-rss span {
    background-color: #FBF8EF;
}

.header .userpic {
    display: none !important;
}

}
