css:
@-moz-document url-prefix("https://www.patria.org.ve/"), url-prefix("https://www.patria.org.ve/login"), url-prefix("https://patria.org.ve/"), url-prefix("https://patria.org.ve/login") {
main{
    background-color: #1F232A;
}
body{
    background-color: #1F232A;
}
h1,h2,h3,h4,h5 {
    color: white;
}
td, th, a, li, ul, html{
    color: white;
}

a.black-text{
    color: white !important;
}

#main-menu ul.right #dropdown1 span,
#main-menu ul.right #dropdown1 span:hover{
    background-color: #272C35;
}
#main-menu ul.right #dropdown1 a {
    color: white;
}

section#section-mobile-app.container{
    background-color: #272C35;
}

section#section-links{
    background-color: #1F232A;
}

#section-login #login{
    background-color: #272C35;
}

#section-login #before-login{
    border-bottom: transparent;
}

div.card-panel{
    background-color:#272C35;
    border-color: #3e4652;
}

#left-side-menu li > a{
    color: white;
}

.dropdown-content{
    background-color: #272C35;
}
aside#right-side-menu #main-notifications .card-content .notification{
    border-top: 1px solid #3e4652;
}
aside#right-side-menu #main-notifications .card-content .notification .notification-content{
    color: white;
}

.social-block a{
    color: white;
}

.material-icons{
    color: white;
}

div.content, div.question{
    color: white;
}

footer{
    background-color: #272C35 !important;
}
}
