css:
@-moz-document url-prefix("https://vanillo.co/"), url-prefix("https://vanillo.co/search") {
body {
    background-color: #292929 !important;
}
.progress-bar {
    background:#ff9d0c !important;
}
.pace-done {
    background-color: #292929 !important;
}
.alert-danger {
    background-color: #212121 !important;
    background:#212121;
}

.text-primary {
    color: #a2a2a2 !important;
}
.card-title {
    margin-bottom: 0.75rem;
    color: #a2a2a2;
}
.bg-secondary {
    background-color: #292929 !important;
}
a {
    color: #a2a2a2 !important;
}
.navbar-logo {
    filter: contrast(0%);
}
.search-bar {
    background-color: #272727 !important;
}
.form-control::placeholder {
    color: rgba(255, 255, 255, 0.4);
    opacity: 1;
}
.search-btn {
    background-color: #363636 !important;
    border-color: #fff !important;
    color: #a89d82 !important;
}
.bg-vanillo {
    background: #212121 !important;
}
.nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link {
    background-color: #323130 !important;
}
h1, h2, h3, h4, h5, h6 {
    color:#a2a2a2;
}
.card-body {
    background-color: #212121 !important;
}
p {
    color:#a2a2a2;
}
#about {
    color:#a2a2a2;
}
.bg-primary {
    background-color: #212121 !important;
}
.bg-color {
    background: #323130 !important;
}
}

@-moz-document url-prefix("https://vanillo.co/v/") {
body {
    background-color: #292929 !important;
}
.pace-done {
    background-color: #292929 !important;
}
.bg-primary {
    background-color: #212121 !important;
}
.navbar-logo {
    filter: contrast(0%);
}
.search-bar {
    background-color: #272727 !important;
}
.form-control::placeholder {
    color: rgba(255, 255, 255, 0.4);
    opacity: 1;
}
.search-btn {
    background-color: #363636 !important;
    border-color: #fff !important;
    color: #a89d82 !important;
}
a, p {
    color: #a2a2a2 !important;
}
.card-body {
    background-color: #212121 !important;
}
.bg-secondary {
    background-color: #292929 !important;
}
.mb-2, .my-2 {
    color: #a2a2a2 !important;
}
h5, .h5 {
    color:#a2a2a2;
}
.bg-white {
    background-color: #212121 !important;
}
.btn-secondary {
    background:#e8c266;
}
.bg-color {
    background: #323130 !important;
}
}
