css:
@-moz-document regexp(".*5ch\\.net.*read\\.cgi.*") {
body {
    display: flex;
    flex-direction: column;
    justify-content: center;
}


.container {
    display: flex;
    flex-direction: column;
    justify-content: center;
    margin-right: 0;
    
    background-image: none !important;
}

.thread {
    display: flex;
    flex-direction: column;
    justify-content: center;
    box-sizing: content-box;
    align-self: center;
    margin-top: 50px;
    margin-bottom: 50px;
}

.post {
    box-sizing: content-box;
    width: auto;
    align-self: flex-start;
}


}
