css:
@-moz-document domain("paste.ubuntu.com") {
#content, #id_content,
.paste pre {
	font-family: consolas;
	font-size: 1.5em;
}

div.paste {
	border-radius: 0px;
	margin-left: 30px;
	margin-right: 30px;
}
div#contentColumn {
	border-radius: 0px;
	margin: 40px;
	background-color: rgb(250, 250, 250);
}

#title {
	margin-top: 20px;
	
}

body {
	background-image: none;
	background-color: rgb(240, 242, 245);
}

div#outerColumnContainer {
	height: 100%;
}

#pasteform {
	margin: auto 30px;
}

[type="submit"] {
	width: 100px;
	height: 35px;
	background-color: rgb(230, 230, 230);
	border: 0px;
}

[type="submit"]:hover {
	background-color: #dd4814;
	color: white;
}

input,
select,
textarea {
	outline: none;
}

tr {
	vertical-align: top;
}

th {
	text-align: right;
}

.errorlist li {
	color: red;
	list-style-type: none;
}

.errorlist {
	padding-inline-start: 0px;
	margin-top:0px;
	margin-bottom:5px;
}
}
